import React from "react";
import "./Why_Choose_Plastaps.css";
import images from "../../constant/images";
const Why_Choose_Plastaps = () => {
  return (
    <main>
      <div className="Choose_Plastaps_sec">
        <div className="Choose_Plastaps_inner">
          <div className="Choose_Plastaps_us">
            <h1>Company Vision</h1>
            <p style={{ width: "500px" }}>
              At Plastaps, we envision a future where the plastic industry
              thrives while prioritizing sustainability and responsibility. We
              believe that by connecting buyers and sellers in an efficient and
              transparent manner, we can revolutionize the way plastic resins
              are traded. Our vision is to facilitate the adoption of recycled
              resins and regrinded resins, minimizing plastic waste and
              contributing to a greener planet.
            </p>
          </div>
          <div className="Choose_Plastaps_img">
            <img src={images.aboutus} className="Choose_Plastaps_image_size" />
          </div>
        </div>
      </div>
    </main>
  );
};

export default Why_Choose_Plastaps;
